import React, { Component } from "react";
import {
  StyleSheet,
  View,
  StatusBar,
  Text,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import DeetsDetails from "./components/DeetsDetail";
import Icon from "react-native-vector-icons/Entypo";

function Deets(props) {
  return (
    <ScrollView style={styles.container}>
      <StatusBar hidden />
      <View style={styles.icon1Row}>
        <TouchableOpacity>
          <Icon name="chevron-thin-left" style={styles.icon1}></Icon>
        </TouchableOpacity>
      </View>
      <DeetsDetails style={styles.deetsDetails}></DeetsDetails>

      <View style={styles.button1Row}>
        <TouchableOpacity style={styles.button1}>
          <Text style={styles.joinGame1}>âœ‹Join game</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button2}>
          <Text style={styles.leaveGame}>Leave Game</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgba(227,214,214,1)",
  },
  deetsDetails: {
    height: 512,
    width: 289,
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    elevation: 20,
    shadowOpacity: 0.35,
    shadowRadius: 25,
    marginTop: 30,
    alignSelf: "center",
  },
  icon1: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
  },

  icon1Row: {
    height: 33,
    flexDirection: "row",
    alignContent: "flex-start",
    marginTop: 50,
    marginLeft: 20,
  },
  button1: {
    width: 129,
    height: 51,
    backgroundColor: "rgba(34,40,49,1)",
    borderRadius: 12,
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    elevation: 5,
    shadowOpacity: 0.3,
    shadowRadius: 25,
    marginBottom: 20,
    alignContent: "center",
    justifyContent: "center",
  },
  joinGame1: {
    fontFamily: "roboto-300",
    color: "rgba(255,255,255,1)",
    fontSize: 15,
    alignSelf: "center",
  },
  button2: {
    width: 129,
    height: 51,
    borderRadius: 12,
    borderWidth: 4,
    borderColor: "rgba(34,40,49,1)",
    marginLeft: 30,
    alignContent: "center",
    justifyContent: "center",
  },
  leaveGame: {
    fontFamily: "roboto-300",
    color: "rgba(255,0,0,1)",
    fontSize: 15,
    fontWeight: "bold",
    alignSelf: "center",
  },
  button1Row: {
    height: 51,
    flexDirection: "row",
    marginTop: 20,
    alignSelf: "center",
    marginBottom: 20,
  },
});

export default Deets;
