import React, { Component } from "react";
import {
  StyleSheet,
  View,
  StatusBar,
  Text,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import Ticket from "./components/Ticket";
import Tile from "../shared/Tile";
import Previous from "./components/Previous";
import BottomBar from "./components/BottomBar";

function Game(props) {
  return (
    <React.Fragment>
      <ScrollView style={styles.container}>
        <StatusBar hidden />
        <Previous />
        <Tile style={styles.tile} />
        <Text style={styles.numberCall}>Four Two </Text>
        <Ticket style={styles.ticket}></Ticket>
      </ScrollView>
      <View style={styles.container1}>
        <View style={styles.bottomView}>
          <BottomBar />
        </View>
      </View>
    </React.Fragment>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgba(227,214,214,1)",
  },

  bottomView: {
    width: "100%",
    height: 50,
    justifyContent: "center",
    marginBottom: 50,
    alignItems: "center",
    position: "absolute", //Here is the trick
    bottom: 0, //Here is the trick
  },
  ticket: {
    height: 200,
    width: 400,
    borderRadius: 30,
    marginTop: 50,
    marginBottom: 20,
    alignSelf: "center",
  },

  numberCall: {
    fontFamily: "roboto-700",
    color: "#121212",
    fontSize: 20,
    marginTop: 24,
    alignSelf: "center",
  },

  button1: {
    width: 67,
    height: 67,
    backgroundColor: "rgba(48,71,94,1)",
    borderRadius: 100,
    alignSelf: "center",
  },
  shout: {
    fontFamily: "roboto-500",
    color: "rgba(255,255,255,1)",
    fontSize: 34,
    marginTop: 10,
    marginLeft: 12,
  },
});

export default Game;
