import React, { Component, useState } from "react";
import {
  StyleSheet,
  View,
  StatusBar,
  Text,
  TextInput,
  ScrollView,
  TouchableOpacity,
} from "react-native";

import env from "../../variable";
import EntypoIcon from "react-native-vector-icons/Entypo";
import { useNavigation } from "@react-navigation/native";
import Axios from "axios";
import Global from "../../utility/Global";
const AsyncStore = require("../../utility/AsyncStore");

function SignUp(props) {
  const navigation = useNavigation();
  const [name, setname] = useState("");
  const [email, setemail] = useState("");
  const [phone, setphone] = useState("");
  const [username, setusername] = useState("");
  const [password, setpassword] = useState("");

  const handlePress = () => {
    Axios.post(env.apiUrl + "/user/signup", {
      name: name,
      email: email,
      phone: phone,
      username: username,
      password: password,
    })
      .then((res) => {
        const data = res.data;
        if (data === true) {
          Global.username = username;
          Global.isLoggedin = true;
          AsyncStore.storeData(Global);
          navigation.replace("Feed");
        } else {
          alert(
            "Something Went Wrong. Please use a diffrent Username or check your network connection "
          );
        }
      })
      .catch((err) => {
        alert("There is a network issue");
        console.log(err);
      });
  };

  return (
    <ScrollView style={styles.scrollview} fadingEdgeLength={0}>
      <View style={styles.container}>
        <StatusBar hidden />
        <View style={styles.textGroup}>
          <Text style={styles.heyThere}>Hey There!ðŸ‘‹</Text>
          <Text style={styles.subHeading}>Let&#39;s start by knowing you</Text>
        </View>
        <View style={styles.rect}>
          <TextInput
            placeholder="Name"
            selectionColor="rgba(242,163,101,1)"
            style={styles.name}
            onChangeText={(text) => setname(text)}
          ></TextInput>
          <TextInput
            placeholder="Email"
            selectionColor="rgba(242,163,101,1)"
            style={styles.name1}
            onChangeText={(text) => setemail(text)}
          ></TextInput>
          <TextInput
            placeholder="Phone number"
            selectionColor="rgba(242,163,101,1)"
            style={styles.name2}
            onChangeText={(text) => setphone(text)}
          ></TextInput>
          <TextInput
            placeholder="Username"
            selectionColor="rgba(242,163,101,1)"
            style={styles.name4}
            onChangeText={(text) => setusername(text)}
          ></TextInput>
          <TextInput
            placeholder="Password"
            selectionColor="rgba(242,163,101,1)"
            secureTextEntry={true}
            style={styles.name3}
            onChangeText={(text) => setpassword(text)}
          ></TextInput>
        </View>
        <TouchableOpacity style={styles.button} onPress={handlePress}>
          <EntypoIcon
            name="chevron-thin-right"
            style={styles.icon}
          ></EntypoIcon>
        </TouchableOpacity>

        <View style={styles.icon2Row}>
          <TouchableOpacity
            onPress={() => {
              navigation.replace("Landing");
            }}
          >
            <EntypoIcon
              name="chevron-thin-left"
              style={styles.icon2}
            ></EntypoIcon>
          </TouchableOpacity>
        </View>

        <View style={styles.loremIpsum3Column}>
          <Text style={styles.loremIpsum3}>Already have an account?</Text>
          <TouchableOpacity
            style={styles.button2}
            onPress={() => {
              navigation.navigate("Login");
            }}
          >
            <Text style={styles.login}>Login</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
}

