import React, { Component } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";

function Session(props) {
  var string = "";
  for (var i = 0; i < 10; i++) {
    string += props.deets.description[i];
  }
  string += "...";
  //console.log(string);
  return (
    <View style={[styles.container, props.style]}>
      <Text style={styles.gameName}>
        {props.deets.Roomname + " (" + props.type + ")"}
      </Text>
      <Text style={styles.roomid}>
        #{props.roomID} {props.isActive ? "- Now Playing" : ""}
      </Text>

      <Text style={styles.description}>{string}</Text>
      <View style={styles.buttonRow}>
        <TouchableOpacity style={styles.button1} onPress={props.openDeets}>
          <Text style={styles.deets}>ðŸ”ŽDeets</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 10,
  },
  gameName: {
    fontFamily: "roboto-500",
    color: "#121212",
    fontSize: 20,
    marginTop: 15,
    marginLeft: 14,
  },
  roomid: {
    fontFamily: "roboto-regular",
    color: "#121212",
    fontSize: 14,
    marginTop: 4,
    marginLeft: 14,
  },
  description: {
    fontFamily: "roboto-regular",
    color: "#121212",
    textAlign: "justify",
    color: "rgba(0, 0, 0, 0.68)",
    fontSize: 14,
    marginTop: 16,
    marginLeft: 14,
    marginRight: 14,
  },
  button: {
    width: 83,
    height: 30,
    backgroundColor: "rgba(34,40,49,1)",
    borderRadius: 12,
    justifyContent: "center",
    alignContent: "center",
  },
  joinGame: {
    fontFamily: "roboto-500",
    color: "rgba(255,255,255,1)",
    fontSize: 10,
    alignSelf: "center",
  },
  button1: {
    width: 83,
    height: 30,
    backgroundColor: "rgba(48,71,94,1)",
    borderRadius: 7,
    marginLeft: 0,
    justifyContent: "center",
    alignContent: "center",
  },
  deets: {
    fontFamily: "roboto-500",
    color: "rgba(255,255,255,1)",
    fontSize: 10,
    alignSelf: "center",
  },
  buttonRow: {
    height: 30,
    flexDirection: "row",
    marginTop: 18,
    marginLeft: 14,
    marginRight: 109,
  },
});

export default Session;
