import React, { Component, useState } from "react";
import {
  StyleSheet,
  View,
  StatusBar,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import Axios from "axios";
import Global from "../../utility/Global";
import env from "../../variable";
import { useNavigation } from "@react-navigation/native";

function RoomCreation(props) {
  const [roomid, setroomid] = useState("");
  const [type, settype] = useState("");
  const [maxuser, setmaxuser] = useState(10);
  const [roomName, setroomName] = useState("");
  const [description, setdescription] = useState("");
  const [password, setpassword] = useState("");
  const navigation = useNavigation();
  const capitalize = (s) => {
    if (typeof s !== "string") return "";
    return s.charAt(0).toUpperCase() + s.slice(1);
  };

  const handlePress = () => {
    if (
      roomid.length === 0 ||
      type.length === 0 ||
      maxuser === 0 ||
      roomName.length === 0 ||
      description.length === 0 ||
      password.length === 0
    ) {
      alert("Please fill in all the fields");
    } else if (
      type === "Open" ||
      type === "Private" ||
      type === "open" ||
      type === "private"
    ) {
      Axios.post(env.apiUrl + "/admin/createSession", {
        roomID: roomid,
        type: capitalize(type),
        user: Global.username,
        settings: { category: ["FH", "FR", "SR", "TR"], maxUsers: maxuser },
        deets: { Roomname: roomName, description: description },
        secretKey: password,
      }).then((res) => {
        const data = res.data;
        if (data === true) {
          alert("Room Created!");
          navigation.replace("Feed");
        } else {
          alert(
            "Error in making room: The RoomID specified has already been used, choose another RoomID  "
          );
        }
      });
    } else {
      alert("Error in creating session: Check all your fields");
    }
  };

  return (
    <ScrollView style={styles.container}>
      <StatusBar hidden />
      <Text style={styles.createRoom}>Create Room</Text>
      <Text style={styles.loremIpsum1}>Live sessions to host Tambola</Text>

      <View style={styles.wrap}>
        <TextInput
          placeholder="Room Name"
          style={styles.placeholder}
          onChangeText={(text) => {
            setroomName(text);
          }}
        ></TextInput>

        <TextInput
          placeholder="RoomID"
          style={styles.placeholder}
          onChangeText={(text) => {
            setroomid(text);
          }}
        ></TextInput>
        <TextInput
          placeholder="Type(type Open or Private)"
          style={styles.placeholder}
          onChangeText={(text) => {
            settype(text);
          }}
        ></TextInput>
        <TextInput
          placeholder="Room Password(Leave empty if Open Room)"
          style={styles.placeholder}
          onChangeText={(text) => {
            setpassword(text);
          }}
        ></TextInput>
        <TextInput
          placeholder="Maximum Users(should be more than 10)"
          style={styles.placeholder}
          onChangeText={(text) => {
            setmaxuser(text);
          }}
        ></TextInput>
        <TextInput
          placeholder="Description"
          multiline
          style={styles.placeholderd}
          onChangeText={(text) => {
            setdescription(text);
          }}
        ></TextInput>

        <TouchableOpacity style={styles.button1} onPress={handlePress}>
          <Text style={styles.createRoom2}>Create Room</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgba(227,214,214,1)",
  },
  wrap: {
    alignSelf: "center",
  },
  loremIpsum1: {
    fontFamily: "roboto-regular",
    color: "#121212",
    marginTop: 5,
    marginLeft: 24,
  },
  createRoom: {
    fontFamily: "roboto-900",
    color: "#121212",
    fontSize: 25,
    marginTop: 35,
    marginLeft: 23,
  },
  placeholder: {
    fontFamily: "roboto-regular",
    color: "#121212",
    height: 45,
    width: 309,
    backgroundColor: "rgba(230, 230, 230,1)",
    borderRadius: 15,
    margin: 20,
    textAlign: "center",
  },
  placeholderd: {
    fontFamily: "roboto-regular",
    color: "#121212",
    height: 100,
    width: 309,
    backgroundColor: "rgba(230, 230, 230,1)",
    borderRadius: 15,
    margin: 20,
    textAlign: "center",
  },

  button1: {
    width: 140,
    height: 60,
    alignSelf: "center",
    backgroundColor: "rgba(242,163,101,1)",
    borderRadius: 9,
    marginBottom: 20,
  },
  createRoom2: {
    fontFamily: "roboto-700",
    color: "rgba(255,255,255,1)",
    fontSize: 20,
    marginTop: 18,
    marginLeft: 12,
  },
});

export default RoomCreation;
