import React, { Component } from "react";
import { StyleSheet, View, TouchableOpacity, Text } from "react-native";

function Ticket(props) {
  const row = [];
  const row2 = [];
  for (var i = 0; i < 9; i++) {
    row.push(
      <TouchableOpacity style={styles.button}>
        <Text style={styles.markedText}>{i}</Text>
      </TouchableOpacity>
    );
  }
  for (var i = 0; i < 8; i++) {
    row2.push(
      <TouchableOpacity style={styles.button5}>
        <Text style={styles.notMarkedText}>10</Text>
      </TouchableOpacity>
    );
  }
  return (
    <View style={[styles.container, props.style]}>
      <View style={styles.buttonRow}>{row2}</View>
      <View style={styles.button1Row}>{row2}</View>
      <View style={styles.button2Row}>{row2}</View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
  },
  button: {
    width: 40,
    height: 40,
    backgroundColor: "rgba(0,0,0,1)",
    borderWidth: 2,
    borderColor: "rgba(242,163,101,1)",
    borderRadius: 34,
    marginLeft: 2,
    marginRight: 2,
  },
  markedText: {
    fontFamily: "roboto-700",
    color: "rgba(255,255,255,1)",
    marginTop: 11,
    marginLeft: 9,
  },
  button5: {
    width: 42,
    height: 42,
    backgroundColor: "rgba(255,255,255,1)",
    borderWidth: 2,
    borderColor: "rgba(242,163,101,1)",
    borderRadius: 34,
    marginLeft: 3,
    marginRight: 3,
  },
  notMarkedText: {
    fontFamily: "roboto-700",
    color: "rgba(0,0,0,1)",
    marginTop: 10,
    marginLeft: 10,
  },

  buttonRow: {
    height: 36,
    flexDirection: "row",
    marginTop: 17,
    marginLeft: 9,
    marginRight: 11,
  },

  button1Row: {
    height: 36,
    flexDirection: "row",
    marginTop: 26,
    marginLeft: 9,
    marginRight: 11,
  },

  button2Row: {
    height: 36,
    flexDirection: "row",
    marginTop: 25,
    marginLeft: 9,
    marginRight: 11,
  },
});

export default Ticket;
